import React, { useState } from "react";
import {
  Box,
  Typography,
  List,
  ListItem,
  ListItemText,
  TextField,
  Button,
  Paper
} from "@mui/material";
import { Friend, Message } from "./types";

const friends: Friend[] = [
  { id: "1", name: "Alice" },
  { id: "2", name: "Bob" },
  { id: "3", name: "Charlie" }
];

const App: React.FC = () => {
  const [selectedFriend, setSelectedFriend] = useState<Friend | null>(
    friends[0]
  );
  const [messages, setMessages] = useState<{ [friendId: string]: Message[] }>(
    {}
  );
  const [input, setInput] = useState("");

  const handleSend = () => {
    if (!input.trim() || !selectedFriend) return;

    setMessages(prev => ({
      ...prev,
      [selectedFriend.id]: [
        ...(prev[selectedFriend.id] || []),
        { sender: "me", text: input }
      ]
    }));
    setInput("");
  };

  const currentMessages = selectedFriend
    ? messages[selectedFriend.id] || []
    : [];

  return (
    <Box display="flex" height="100vh">
      {/* Sidebar */}
      <Box width="200px" bgcolor="#f5f5f5" p={2}>
        <Typography variant="h6">Friends</Typography>
        <List>
          {friends.map(friend => (
            <div
              key={friend.id}
              // button
              // selected={selectedFriend?.id === friend.id}
              onClick={() => setSelectedFriend(friend)}
            >
              <>{friend.name}</>
            </div>
          ))}
        </List>
      </Box>

      {/* Chat Window */}
      <Box flex={1} p={2} display="flex" flexDirection="column">
        <Typography variant="h6" gutterBottom>
          Chat with {selectedFriend?.name}
        </Typography>

        <Paper
          variant="outlined"
          sx={{
            flex: 1,
            p: 2,
            overflowY: "auto",
            mb: 2,
            backgroundColor: "#fafafa"
          }}
        >
          {currentMessages.map((msg, index) => (
            <Box
              key={index}
              textAlign={msg.sender === "me" ? "right" : "left"}
              mb={1}
            >
              <Typography
                variant="body2"
                sx={{
                  display: "inline-block",
                  px: 1.5,
                  py: 1,
                  borderRadius: 2,
                  backgroundColor: msg.sender === "me" ? "#1976d2" : "#e0e0e0",
                  color: msg.sender === "me" ? "#fff" : "#000"
                }}
              >
                {msg.text}
              </Typography>
            </Box>
          ))}
        </Paper>

        {/* Input */}
        <Box display="flex" gap={1}>
          <TextField
            fullWidth
            size="small"
            placeholder="Type a message"
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={e => {
              if (e.key === "Enter") handleSend();
            }}
          />
          <Button variant="contained" onClick={handleSend}>
            Send
          </Button>
        </Box>
      </Box>
    </Box>
  );
};

export default App;
