import { styled } from "@mui/material/styles";
import { Box, Typography, IconButton } from "@mui/material";

export const ChatContainer = styled(Box)(({ theme }) => ({
  position: "fixed",
  bottom: 0,
  right: 20,
  height: "75vh",
  width: "25vw",
  maxWidth: 360,
  backgroundColor: "#fff",
  boxShadow: theme.shadows[4],
  borderRadius: 8,
  display: "flex",
  flexDirection: "column",
  zIndex: 1300
}));

export const FriendList = styled(Box)(({ theme }) => ({
  width: "200px",
  height: "100%",
  backgroundColor: "#f5f5f5",
  padding: theme.spacing(2),
  overflowY: "auto",
  boxShadow: theme.shadows[4], // ✨ Add shadow
  borderRight: "1px solid #ddd"
}));

export const ChatHeader = styled(Box)(({ theme }) => ({
  display: "flex",
  alignItems: "center",
  justifyContent: "space-between",
  padding: theme.spacing(1.5),
  backgroundColor: "#f1f1f1",
  borderBottom: "1px solid #ddd",
  borderTopLeftRadius: 8,
  borderTopRightRadius: 8
}));

export const ChatContent = styled(Box)(({ theme }) => ({
  flex: 1,
  overflowY: "auto",
  padding: theme.spacing(2)
}));

export const ChatInputArea = styled(Box)(({ theme }) => ({
  padding: theme.spacing(1.5),
  borderTop: "1px solid #ddd"
}));
