import React, { useState } from "react";
import {
  Box,
  Typography,
  List,
  ListItem,
  ListItemAvatar,
  Avatar,
  ListItemText
} from "@mui/material";
import { Friend, Message } from "../types";
import KeyboardArrowUpIcon from "@mui/icons-material/KeyboardArrowUp";
import KeyboardArrowDownIcon from "@mui/icons-material/KeyboardArrowDown";

export default function FriendsList(props: {
  friends: Friend[];
  setSelectedFriend: any;
}) {
  const { friends, setSelectedFriend } = props;
  const [showList, setShowList] = useState<boolean>(false);
  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map(word => word[0])
      .join("")
      .toUpperCase();
  };
  return (
    <Box
      width="200px"
      zIndex={2}
      height={showList ? "80%" : "5%"}
      position={"fixed"}
      bottom={0}
      margin={"0 auto"}
      bgcolor="#f5f5f5"
      p={2}
    >
      <Box
        style={{ display: "flex", justifyContent: "space-between" }}
        onClick={() => setShowList(prev => !prev)}
      >
        <Typography variant="h6">Friends</Typography>
        {showList ? <KeyboardArrowDownIcon /> : <KeyboardArrowUpIcon />}
      </Box>
      {showList && (
        <List>
          {friends.map((friend: Friend) => {
            const lastMessage = messages[friend.id]?.slice(-1)[0];
            const previewText = lastMessage
              ? lastMessage.text.slice(0, 30) +
                (lastMessage.text.length > 30 ? "…" : "")
              : "";
            const time = lastMessage ? format(new Date(), "p") : ""; // Replace `new Date()` with actual timestamp if available
            return (
              <ListItem
                key={friend.id}
                // button
                // selected={selectedFriend?.id === friend.id}
                onClick={() => setSelectedFriend(friend)}
                sx={{ cursor: "pointer" }}
              >
                <ListItemAvatar>
                  <Avatar>{getInitials(friend.name)}</Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={
                    <Box
                      display="flex"
                      justifyContent="space-between"
                      alignItems="center"
                    >
                      <Typography fontWeight={600}>{friend.name}</Typography>
                      <Typography variant="caption" color="text.secondary">
                        {time}
                      </Typography>
                    </Box>
                  }
                  secondary={
                    <Typography variant="body2" color="text.secondary" noWrap>
                      {previewText || "No messages yet"}
                    </Typography>
                  }
                />
              </ListItem>
            );
          })}
        </List>
      )}
    </Box>
  );
}
